package main

import "fmt"

func FindStartPacket(s string, size int) int {
	for i := 0; i < len(s)-size-1; i++ {
		substr := s[i : i+size]
		set := map[string]bool{}
		for i2 := range substr {
			set[substr[i2:i2+1]] = true
		}

		if len(set) == size {
			return i + size
		}
	}

	return -1
}

func main() {
	inputString := ""
	fmt.Printf("Marker packet %d\n", FindStartPacket(inputString, 4))
	fmt.Printf("Message packet %d\n", FindStartPacket(inputString, 14))
}
